import React, { useState, useEffect } from 'react';
import Select from 'react-select'; // ALTERAÇÃO 1: Importar o componente Select
import { supabase } from '../../supabaseClient';
import { useAuth } from '../../contexts/AuthContext';
import axios from 'axios';
// Reutiliza o CSS do formulário de cliente
import '../../components/ClientForm/ClientForm.css';

// ALTERAÇÃO 2: Definir as opções de serviço no formato que o react-select espera
const servicosOptions = [
  { value: "Limpeza de Pele Profunda", label: "Limpeza de Pele Profunda" },
  { value: "Peeling de Diamante", label: "Peeling de Diamante" },
  { value: "Microagulhamento", label: "Microagulhamento" },
  { value: "Drenagem Linfática Facial", label: "Drenagem Linfática Facial" },
  { value: "Massagem Modeladora", label: "Massagem Modeladora" },
  { value: "Design de Sobrancelhas", label: "Design de Sobrancelhas" },
  { value: "Aplicação de Cílios", label: "Aplicação de Cílios" },
  { value: "Manicure e Pedicure", label: "Manicure e Pedicure" },
  { value: "Depilação a Laser", label: "Depilação a Laser" },
  { value: "Radiofrequência", label: "Radiofrequência" },
  { value: "Criolipólise", label: "Criolipólise" },
  { value: "Consulta de Avaliação", label: "Consulta de Avaliação" }
];


export default function RealizarVendaPage() {
    const { user } = useAuth();
    const [clients, setClients] = useState([]);
    const [formData, setFormData] = useState({
        client_id: '',
        // ALTERAÇÃO 3: O estado inicial agora é um array vazio para o multi-select
        service_description: [], 
        value: '',
        payment_method: '',
        installments: 1
    });
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        async function fetchClients() {
            const { data } = await supabase.from('clients').select('id, name').order('name');
            setClients(data || []);
        }
        fetchClients();
    }, []);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    // ALTERAÇÃO 4: Criar um handler separado para o react-select
    const handleSelectChange = (selectedOptions) => {
        setFormData(prev => ({ ...prev, service_description: selectedOptions || [] }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        try {
            // ALTERAÇÃO 5: Transformar o array de serviços em uma string para salvar no banco
            const descriptionString = formData.service_description.map(option => option.label).join(', ');

            const dataToSave = {
                ...formData,
                service_description: descriptionString,
            };

            // 1. Salva a venda no Supabase usando os dados formatados
            const { data: sale, error } = await supabase
                .from('sales')
                .insert({ ...dataToSave, user_id: user.id, status: 'Pendente' })
                .select()
                .single();
            if (error) throw error;
            
            alert('Venda registrada! Enviando para emissão da NFS-e...');

            // 2. Chama o backend para emitir a nota
            const backendUrl = process.env.REACT_APP_BACKEND_URL;
            await axios.post(`${backendUrl}/api/nfse/emitir`, {
                saleId: sale.id,
                clientId: sale.client_id,
            });

            alert('NFS-e enviada para processamento!');
            // ALTERAÇÃO 6: Resetar o formulário com o estado correto para o select
            setFormData({ client_id: '', service_description: [], value: '', payment_method: '', installments: 1 });
        } catch(error) {
            alert(`Erro: ${error.message}`);
        } finally {
            setLoading(false);
        }
    };
    
    return (
        <div className="page-container">
            <div className="page-header">
                <h1>Realizar Venda</h1>
            </div>
            <div className="page-card">
                <form onSubmit={handleSubmit} className="client-form">
                    <label>Cliente</label>
                    <select name="client_id" value={formData.client_id} onChange={handleChange} required>
                        <option value="" disabled>Selecione um cliente</option>
                        {clients.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}
                    </select>

                    <label>Descrição do Serviço</label>
                    {/* ALTERAÇÃO 7: Substituir o <textarea> pelo componente <Select> */}
                    <Select
                        isMulti
                        name="service_description"
                        options={servicosOptions}
                        className="basic-multi-select"
                        classNamePrefix="select"
                        placeholder="Selecione os serviços..."
                        value={formData.service_description}
                        onChange={handleSelectChange}
                        required
                    />
                    
                    <label>Valor (R$)</label>
                    <input type="number" step="0.01" name="value" value={formData.value} onChange={handleChange} required />
                    
                    <label>Forma de Pagamento</label>
                    <select name="payment_method" value={formData.payment_method} onChange={handleChange} required>
                        <option value="" disabled>Selecione</option>
                        <option value="Dinheiro">Dinheiro</option>
                        <option value="Pix">Pix</option>
                        <option value="Cartão de Débito">Cartão de Débito</option>
                        <option value="Cartão de Crédito">Cartão de Crédito</option>
                    </select>

                    <div className="form-actions">
                        <button type="submit" className="page-button-primary" disabled={loading}>
                            {loading ? 'Processando...' : 'Finalizar Venda e Emitir Nota'}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}
